con:config.script:
def response = context.expand('${GetCountryList#Response}')
def xml = new XmlSlurper(false, false).parseText(response)
xml.declareNamespace(
    soap: 'http://schemas.xmlsoap.org/soap/envelope/',
    m: 'http://www.oorsprong.org/websamples.countryinfo'
)

// ISO-codes
def isoCodes = xml.'soap:Body'
    .'m:ListOfCountryNamesByCodeResponse'
    .'m:ListOfCountryNamesByCodeResult'
    .'m:tCountryCodeAndName'
    .collect { it.'m:sISOCode'.text() }
    .findAll { it }

// Save to TestCase
context.testCase.setPropertyValue("CountryCodes", isoCodes.join(','))
context.testCase.setPropertyValue("CountryIndex", "0")

def codes = context.testCase.getPropertyValue("CountryCodes").split(',')
def index = context.testCase.getPropertyValue("CountryIndex") as int

log.info "Iteration $index / ${codes.size()}"

if (index < codes.size()) {
    def currentISO = codes[index]
    context.testCase.setPropertyValue("CurrentISO", currentISO)
    testRunner.gotoStepByName("FullCountryInfo")
} else {
    testRunner.gotoStepByName("END")  // Plug to exit loop
}

def response = context.expand('${FullCountryInfo#Response}')
def xml = new XmlSlurper(false, false).parseText(response)

xml.declareNamespace(
    soap: 'http://schemas.xmlsoap.org/soap/envelope/',
    m: 'http://www.oorsprong.org/websamples.countryinfo'
)

def result = xml.'soap:Body'
               .'m:FullCountryInfoResponse'
               .'m:FullCountryInfoResult'

def expectedISO = context.expand('${#TestCase#CurrentISO}')
def actualISO = result.'m:sISOCode'.text()
def countryName = result.'m:sName'.text()

// tests
assert actualISO == expectedISO : "ISO: Expected $expectedISO, got $actualISO"
assert countryName : "Country name mismatch"

log.info "$expectedISO: $countryName"

def i = context.testCase.getPropertyValue("CountryIndex") as int
context.testCase.setPropertyValue("CountryIndex", (i + 1).toString())
testRunner.gotoStepByName("LoopController")
